'use strict';

var Button = require('../index');

var button1 = new Button({
  text: 'button1',
  id: 'button1',
  style: {
    'background-color': 'blue',
    'height': '50px',
    'border': 'none',
    'width': '100px'
  },
  onclick: function(button) {
    console.log(button.id); 
  }
});

button1.mountToElementById('my-button');

